import React from 'react'
import { TypeAnimation } from 'react-type-animation';

const Banner = () => {
  return (
    <div className='grid grid-cols-2 px-20'>
      <div className='typeanimation'>
      <TypeAnimation
      sequence={[
        // Same substring at the start will only be typed out once, initially
       'Welcome To Travelbound',
        1000,
      ]}
      wrapper="span"
      speed={50}
      style={{ fontFamily: 'Raleway', fontSize: '80px', display: 'inline-block', color: '#4ade80', fontStyle: 'bold' }}
      repeat={Infinity}
    />
       <div>
    <h3 className='flex justify-start font-bold text-[24px] text-start my-10'>Indulge in an immersive experience of nature, culture and history</h3>
    </div>
    <div className='header_button inline-flex justify-start'>
      <button className='px-8 p-3 outline rounded-3xl mx-5 justify-normal align-middle hover:bg-lime-300 transition ease-out duration-75 delay-75 transform hover:translate-y-1 hover:scale-110'>Book Now</button>
      <button className='px-8 p-3 outline rounded-3xl mx-5 justify-normal align-middle hover:bg-lime-300 transition ease-out duration-75 delay-75 transform hover:translate-y-1 hover:scale-110'>Contact</button>
      </div>
      </div>
    <img src='/images/rajshahi streets.jpg' alt='rajshahi-streets' className='h-90 w-100 rounded-3xl my-20 mx-20 drop-shadow-2xl'></img>
      </div>  )
}

export default Banner
